#include<bits/stdc++.h>
using namespace std;typedef long long LL;const int MAXN=51;double dp[MAXN][MAXN][MAXN][MAXN];double s[MAXN];class RockPaperScissors{public:double bestScore(vector<int>A,vector<int>B,vector<int>C){const int N=A.size();for(int k=0;k<=N;++k){double(*f)[MAXN][MAXN]=::dp[k];f[0][0][0]=1;int n=0;for(int i=0;i!=N;++i)if(i!=k){double pa=A[i]/300.0,pb=B[i]/300.0,pc=C[i]/300.0;for(int a=n;a>=0;--a)for(int b=n-a;b>=0;--b)for(int c=n-a-b;c>=0;--c){double v=f[a][b][c];f[a+1][b][c]+=v*pa;f[a][b+1][c]+=v*pb;f[a][b][c+1]+=v*pc;}++n;}}for(int a=N-1;a>=0;--a)for(int b=N-1-a;b>=0;--b)for(int c=N-1-a-b;c>=0;--c)s[a+b+c]+=dp[N][a][b][c];double ret=0;for(int a=N-1;a>=0;--a)for(int b=N-1-a;b>=0;--b)for(int c=N-1-a-b;c>=0;--c){double ea=0,eb=0,ec=0;for(int i=0;i!=N;++i){double p=dp[i][a][b][c];ea+=p*(A[i]+3*C[i]);eb+=p*(B[i]+3*A[i]);ec+=p*(C[i]+3*B[i]);}ret+=max(max(ea,eb),ec)/s[a+b+c]/(N-a-b-c);}return ret/300;}};
