#include<bits/stdc++.h>
#define MOD 1000000007
using namespace std;const int MAXN=1000010;int qpow(int a,int b){int tmp=a,ret=1;for(;b;b>>=1,tmp=1LL*tmp*tmp%MOD)if(b&1)ret=1LL*ret*tmp%MOD;return ret;}int Fac[MAXN],Inv[MAXN];void Init(int N){Fac[0]=1;for(int i=1;i<=N;i++)Fac[i]=1LL*i*Fac[i-1]%MOD;Inv[N]=qpow(Fac[N],MOD-2);for(int i=N;i;i--)Inv[i-1]=1LL*i*Inv[i]%MOD;}int C(int N,int M){if(N<M)return 0;return 1LL*Fac[N]*Inv[M]%MOD*Inv[N-M]%MOD;}int f[MAXN];class LittleElephantAndBoard{public:int getNumber(int M,int R,int G,int B){int x=M-B,y=M-G,z=M-R;if(x<0||y<0||z<0||x>M||y>M||z>M)return 0;if(x<y)swap(x,y);if(x<z)swap(x,z);if(y<z)swap(y,z);Init(M+1);for(int i=0;i<=x+y;i++){if(((i+(x-y))&1)){int dx=(i+(x-y)-1)/2,dy=i-dx;if(dx<0||x-dx<2||dy<0||y-dy<1)continue;f[i]+=1LL*C(x-1,x-dx-1)*C(y-1,y-dy-1)%MOD;f[i]%=MOD;}else{int dx=(i+x-y)/2,dy=i-dx;if(dx<0||x-dx<1||dy<0||y-dy<1)continue;f[i]+=1LL*C(x-1,x-dx-1)*C(y-1,y-dy-1)%MOD;f[i]%=MOD;}}for(int i=0;i<=x+y;i++){if(((i+(x-y))&1)){int dy=(i+(y-x)-1)/2,dx=i-dy;if(dx<0||x-dx<1||dy<0||y-dy<2)continue;f[i]+=1LL*C(x-1,x-dx-1)*C(y-1,y-dy-1)%MOD;f[i]%=MOD;}else{int dx=(i+x-y)/2,dy=i-dx;if(dx<0||x-dx<1||dy<0||y-dy<1)continue;f[i]+=1LL*C(x-1,x-dx-1)*C(y-1,y-dy-1)%MOD;f[i]%=MOD;}}int ans=0;for(int i=0;i<=x+y;i++){if(f[i]){if(i>z)continue;ans+=2LL*f[i]*C(x+y-i+1,z-i)%MOD;if(ans>=MOD)ans-=MOD;}}return ans;}};